import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import { login } from "../redux/apiCalls";
import { mobile } from "../responsive";

const Container = styled.div`
width: 100vw;
height: 100vh;
background: linear-gradient(rgba(255,255,255,0.5), rgba(255,255,255,0.5)), url("https://wallpaperbat.com/img/455728-free-download-womens-clothing-on-a-white-clothes-hanger-on-pink-pastel-1920x1080-for-your-desktop-mobile-tablet-explore-clothing-background-clothing-wallpaper-clothing-background-palace-clothing-wallpaper.png") center;
display: flex;
align-items: center;
justify-content: center;
position: relative;
`;

const Wrapper = styled.div`
position: absolute;
top: 100px;
width: 25%;
padding: 20px;
background-color: white;
${mobile({ width: "75%"})}
`;

const Title = styled.h1`
font-size: 24px;
font-weight: 300;
`;

const Form = styled.form`
display: flex;
flex-direction: column;
`;

const Input = styled.input`
flex: 1;
min-width: 40%;
margin: 10px 0px;
padding: 10px;
`;

const Button = styled.button`
width: 40%;
border: none;
padding: 15px 20px;
background-color: teal;
color: white;
cursor: pointer;
margin-bottom: 10px;
&:disabled{
    color: black;
    cursor: not-allowed;
}
`;

const Link = styled.a`
margin: 5px 0px;
font-size: 12px;
text-decoration: underline;
cursor: pointer;
`;

const Error = styled.span`
color: red;
`;

const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const dispatch = useDispatch();
    const { isFetching, error } = useSelector((state) => state.user);

    const handleClick = (e) => {
        login(dispatch, { username, password });
    };

    return (
        <Container>
            <Wrapper>
                <Title>SIGN IN</Title>
                <Form>
                     <Input 
                     placeholder="Username" 
                     onChange={(e)=>setUsername(e.target.value)}
                     />
                     <Input 
                     placeholder="Password" 
                     type="password"
                     onChange={(e)=>setPassword(e.target.value)}
                     />
                     <Button onClick={handleClick} disabled={isFetching}>LOGIN</Button>
                     {error && <Error>Something went wrong...</Error>}
                     <Link>Forgot your password?</Link>
                     <Link>Register</Link>
                </Form>
            </Wrapper>
        </Container>
    )
}

export default Login
