import axios from "axios";

const BASE_URL = "http://localhost:5000/api/"

const user = JSON.parse(localStorage.getItem(""))

export const publicRequest = axios.create({
    baseURL: BASE_URL,
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    header: {
        token: `Bearer ${TOKEN}`
    },
});
