import {
    createSlice
} from "@reduxjs/toolkit";

const userSlice = createSlice({
    name: "user",
    initialState: {
        currentUser: null,
        isFetching: false,
        error: false,
        userLastSavedCart: {
            products: [
              {
                inStock: true,
                _id: '61dc5944a0b8f0f0a56c2008',
                title: 'Full Suit',
                desc: 'A Beautiful 3 peice suit, made for the modern man. Available in a variety of colours, you\'ll look suave in any occassion.',
                img: 'https://n.nordstrommedia.com/id/sr3/72ef8a2e-6684-4dd9-b582-501f0226009b.jpeg?h=365&w=240&dpr=2',
                categories: [
                  'mens',
                  'suits',
                  'smart'
                ],
                size: 'S',
                color: '',
                createdAt: '2021-01-07T17:51:36.852Z',
                price: '299',
                updatedAt: '2022-01-07T17:51:36.852Z',
                __v: 0,
                quantity: 1
              }
            ],
            quantity: 1,
            total: 299
          },
    },
    reducers: {
        loginStart: (state) => {
            state.isFetching = true;
        },
        loginSuccess: (state, action) => {
            state.isFetching = false;
            state.currentUser = action.payload;
            state.error = false;
        },
        loginFailure: (state) => {
            state.isFetching = false;
            state.error = true;
        },
        logOut: (state) => {
            state.isFetching = false;
            state.error = false;
            state.currentUser = null;
        },
        registerStart: (state) => {
            state.isFetching = true;
        },
        registerSuccess: (state, action) => {
            state.isFetching = false;
            state.currentUser = action.payload;
            state.error = false;
        },
        registerFailure: (state) => {
            state.isFetching = false;
            state.error = true;
        }
    },
});

export const {
    loginStart,
    loginSuccess,
    loginFailure,
    logOut,
    registerStart,
    registerSuccess,
    registerFailure
} = userSlice.actions;

export default userSlice.reducer;
